app.get('/read', (req, res) => {
    // console.log(Products)
    res.send({ ok: true, Products})
})





app.post("/add", (req, res) => {  //the client send somthing to the server (also called CREATE)

    // console.log(req.body)
    if(req.body !== ''){
        let isProductExists = false;
        Products.forEach(product => {
            if (product.name === req.body.name) {
                isProductExists = true
                return;
            };
    
        });
    
        if (!isProductExists) {
            Products.push(req.body);
        };
    
    
        // console.log(users);
    
        res.send({ ok: true, Products })
    }
 
})

app.put("/update", (req, res) => {  //the client update somthing to the server (also called UPDATE)

        
   
    const { newName, newImgUrl, newPrice, nameToUpdate } = req.body;
    console.log(newName,newImgUrl,newPrice)
    let productToUpdate = nameToUpdate;
    // console.log(newPass, username);
    let productIndex = Products.findIndex(product => product.name === productToUpdate);
    if(newName !== ''){Products[productIndex].name = newName;}
    if(newImgUrl !== ''){Products[productIndex].imgUrl = newImgUrl;}
    if(newPrice !== ''){Products[productIndex].price = newPrice;}

    res.send({ ok: true, Products })
})

app.delete("/delete", (req, res) => {

    const { name } = req.body;

    let productIndex = Products.findIndex(product => product.name === name);

    Products.splice(productIndex, 1); 

    // console.log(Products);

    res.send({ ok: true, Products })
})

app.post('/login', (req, res) => {

    const { username, password } = req.body;

    let isUserExist = false;
    let role = 'denied';

    const indexUser = users.findIndex(user => user.username === username && user.password === password);
    if (indexUser > -1) {
        isUserExist = true;
        role = users[indexUser].role;
    }

    if(role === 'denied'){
        res.cookie('role', role, { maxAge: 20000000, httpOnly: false });
        res.send({ ok: false})
    }else if(role === 'public'){
        res.cookie('role', role, { maxAge: 20000000, httpOnly: false });
        res.send({ ok:true })
    }else if(role === 'admin'){
        res.cookie('role', role, { maxAge: 20000000, httpOnly: false });
        res.send({ ok:true })
    }
    
    })



    app.use((req, res, next) => {

        const { role } = req.cookies
        if (role === 'admin') {
            res.authorized = true;
            console.log( res.authorized)
        }
        next();

    })

 

